import {useState} from "react";
import './App.css';
import ReactDOM from "react-dom/client";
 function Counter() {
     const[counter,setcounter]=useState(0);
     // const [inputValue, setInputValue] = useState("");
     const [name, setName] = useState('')
     // const [list, setList] = useState(getLocalStorage())
     const [isEditing, setIsEditing] = useState(false)
     const [editID, setEditID] = useState(null)
     const [alert, setAlert] = useState({ show: false, msg: '', type: '' })
     const Incress = () => {
         setcounter(counter + 1)
     }
     const Decreese = () => {
         if (counter > 0) {
             setcounter(counter - 1)
         }
     }
     const reset =() => {
         setcounter(0)
     }
     // const changeValue = (todo) => {
     //     setcounter(todo);
     // }
     // const onChangeHandler = event => {
     //     setInputValue(event.target.value);
     // };
     // console.log(onChangeHandler);
     if(name && isEditing) {
       /*  setList(
             list.map((item) => {
                 if(item.id === editID) {
                     return { ...item, title: name }
                 }
                 return item
             })
         )*/
         setName('')
         setEditID(null)
         setIsEditing(false)
     }
     return (
         <div className="counter">
             <h1>React Counter</h1>
             {/*<input type = "number" />*/}
             <input
                 type="number"
             />
             <span className="counter__output">{counter}</span>
             <div className="btn__container">
                 <input type="text" className="todo" placeholder="e.g. check email" value={name} onChange={(e) => setName(e.target.value)} />
                 <button type="submit" className="submit-btn">
                     {isEditing ? 'edit' : 'submit'}
                 </button>
                 <button className="control__btn" onClick={Incress}>+</button>
                 <button className="control__btn" onClick={Decreese}>-</button>
                 <button className="reset" onClick={reset}>Reset</button>
                 {/*<button className="reset"  onClick={onChangeHandler}>Change</button>*/}
             </div>
         </div>
     )
 }
 const root = ReactDOM.createRoot(document.getElementById('mydiv'));
 root.render(<Counter/>);
