import {useState} from "react";
import './App.css';
import ReactDOM from "react-dom/client";
 function Counter() {
     const[counter,setcounter]=useState(0);

     const Incress = () => {
         if (counter > 0) {
             setcounter(counter + 1)
         }
     }
     const Decreese = () => {
         if (counter > 0) {
             setcounter(counter - 1)
         }
     }
     const reset =() => {
         setcounter(0)
     }
     const changeValue = (event) => {
         setcounter(event.target.value.replace(/[+-]/ig, ''));
     }
     return (
         <div className="counter">
             <h1>React Counter</h1>
             {/*<input type = "number" />*/}
             <input
                 type = "text"
                 // name="counter"
                 onChange={changeValue}
                 // value={counter}
             />
             <button className="control__btn"  onChange={changeValue}>Change</button>
             <span className="counter__output">{counter}</span>
             <div className="btn__container">
                 <button className="control__btn" onClick={Decreese}>-</button>
                 <button className="control__btn" onClick={Incress}>+</button>

                 <button className="reset" onClick={reset}>Reset</button>
             </div>
         </div>
     )
 }
 const root = ReactDOM.createRoot(document.getElementById('mydiv'));
 root.render(<Counter/>);
