import {useState} from "react";
import './App.css';
import ReactDOM from "react-dom/client";
 function Counter() {
     const[counter,setcounter]=useState(0);
     const[count,setcount]=useState(0);
/*

     const decreese = () => {
         if (counter > 0) {
             setcounter(counter - 1)
         }
     }

     const incress = () => {
       // console.log(typeof(+counter));
             setcounter(+counter + 1)

     }*/
     const reset =() => {
         setcounter(0)
         setcount(0)
     }
     const changefirstValue = (event) => {
         setcounter(event.target.value);
     }
     const changesecondValue = (event) => {
         setcount(event.target.value);
     }
     return (
         <div className="counter">
             <h1>React Counter</h1>
             {/*first input*/}
             <input
                 type = "number"
                 // name="counter"
                 onChange={changefirstValue}
                 // value={counter}
             />
             {/*second input*/}
             <input
                 type = "number"
                 // name="counter"
                 onChange={changesecondValue}
                 // value={counter}
             />
             {/*<button className="control__btn"  onClick={changeValue}>Change</button>*/}
             <span className="counter__output">{counter}</span>
             <span className="counter__output">{count}</span>
             <div className="btn__container">
      {/*           <button className="control__btn" onClick={decreese}>-</button>
                 <button className="control__btn" onClick={incress}>+</button>*/}

                 <button className="reset" onClick={reset}>Reset</button>
             </div>
         </div>
     )
 }
 const root = ReactDOM.createRoot(document.getElementById('mydiv'));
 root.render(<Counter/>);
