import {useState} from "react";
import './App.css';
import ReactDOM from "react-dom/client";
 function Counter() {
     const[counter,setcounter]=useState(0);
     const Incress = () => {
         setcounter(count=>count+1)
     }
     const Decreese = () => {
         if (counter > 0) {
             setcounter(count => count - 1)
         }
     }
     const reset =() => {
         setcounter(0)
     }
     return (
         // <div>
         <>
             <h1>React Counter</h1>
             <span className="counter__output">{counter}</span>
             <div className="btn__container">
                 <button className="control__btn" onClick={Incress}>+</button>
                 <button className="control__btn" onClick={Decreese}>-</button>
                 <button className="reset" onClick={reset}>Reset</button>
             </div>
             {/*<button onClick={Incress}>+</button>*/}
             {/*<button onClick={Decreese}>-</button>*/}
             {/*<button onClick={reset}>reset</button>*/}
             {/*<span>{counter}</span>*/}
         </>
     )
 }
 const root = ReactDOM.createRoot(document.getElementById('mydiv'));
 root.render(<Counter/>);
