import {useState} from "react";
import './App.css';
import ReactDOM from "react-dom/client";
 function Counter() {
     const[counter,setcounter]=useState(0);
     const Incress = () => {
         setcounter(counter + 1)
     }
     const Decreese = () => {
         if (counter > 0) {
             setcounter(counter - 1)
         }
     }
     const reset =() => {
         setcounter(0)
     }
     const changeValue = (todo) => {
         setcounter(counter.push(todo));
     }
     return (
         <div className="counter">
             <h1>React Counter</h1>
             <input type = "text"/>
             <span className="counter__output">{counter}</span>
             <div className="btn__container">
                 <button className="control__btn" onClick={Incress}>+</button>
                 <button className="control__btn" onClick={Decreese}>-</button>
                 <button className="reset" onClick={reset}>Reset</button>
                 <button className="reset"  onClick={changeValue.todo}>Change</button>
             </div>
         </div>
     )
 }
 const root = ReactDOM.createRoot(document.getElementById('mydiv'));
 root.render(<Counter/>);
